<tr valign="top">    <th scope="row" class="titledesc"><?php _e('Add Shipping Rates', 'woocommerce'); ?>:</th>    <td class="forminp" id="<?php echo $this->id; ?>_wc_cart_rates">        <table class="shippingrows widefat" cellspacing="0">            <thead>            <tr>                <th class="check-column"><input type="checkbox"></th>                <th><?php _e('Condition', 'woocommerce'); ?></th>                <th><?php _e('Condition Value('.get_woocommerce_currency_symbol().')', 'woocommerce'); ?></th>                <th><?php _e('Shipping Cost('.get_woocommerce_currency_symbol().')', 'woocommerce'); ?></th>            </tr>            </thead>            <tbody class="<?php echo $this->id; ?>_wc_cart_rates">            <tr>                <td></td>                <td>                    <select name="default_wc_cart_condition[0]" class="select">                        <option value="0">Select Condition</option>                        <option value="1"<?php                        if (isset($this->wc_cart_rates['condition'][0]) && $this->wc_cart_rates['condition'][0] == 1) {                            echo 'selected';                        }                        ?>>Cart Amount Less than or equal to                        </option>                        <option value="2"<?php                        if (isset($this->wc_cart_rates['condition'][0]) && $this->wc_cart_rates['condition'][0] == 2) {                            echo 'selected';                        }                        ?>>Cart Amount Greater than or equal to                        </option>                    </select>                </td>                <td><input type="text" name="default_wc_cart_condition_value[0]" placeholder="Condition Value" size="20"                           class="wc_input_price" value="<?php                    echo isset($this->wc_cart_rates['value'][0]) ? $this->wc_cart_rates['value'][0] : ''; ?>"/></td>                <td><input type="text" name="default_wc_cart_cost[0]" placeholder="Shipping Cost" size="20"                           class="wc_input_price" value="<?php                    echo isset($this->wc_cart_rates['cart_total'][0]) ? $this->wc_cart_rates['cart_total'][0] : ''; ?>"/>                </td>            </tr>            <?php            if (!empty($this->wc_cart_rates['condition']) && count($this->wc_cart_rates['condition']) > 1) {                for ($i = 1; $i < count($this->wc_cart_rates['condition']); $i++) {                    $selected1 = $this->wc_cart_rates['condition'][$i] == 1 ? 'selected' : '';                    $selected2 = $this->wc_cart_rates['condition'][$i] == 2 ? 'selected' : '';                    $condition_value = $this->wc_cart_rates['value'][$i] ? $this->wc_cart_rates['value'][$i] : 0;                    $cart_cost = $this->wc_cart_rates['cart_total'][$i] ? $this->wc_cart_rates['cart_total'][$i] : 0;                    echo '<tr class="wc_cart_rates">';                    echo '<th class="check-column"><input type="checkbox" name="select[' . $i . ']" /></th>';                    echo '<td class="wc_cart_rates_class">';                    echo '<select name="default_wc_cart_condition[' . $i . ']" class="select">';                    echo '<option value="0">Select Condition</option>';                    echo '<option value="1" ' . $selected1 . '>Cart Amount Less than or equal to</option>';                    echo '<option value="2" ' . $selected2 . '>Cart Amount Greater than or equal to</option>';                    echo '</select>';                    echo '</td>';                    echo '<td><input type="text" name="default_wc_cart_condition_value[' . $i . ']" placeholder="Condition Value" size="20" class="wc_input_price" value="' . wc_format_decimal($condition_value) . '" /></td>';                    echo '<td><input type="text" name="default_wc_cart_cost[' . $i . ']" placeholder="Shipping Cost" size="20" class="wc_input_price" value="' . wc_format_decimal($cart_cost) . '" /></td>';                    echo '</tr>';                }            }            ?>            </tbody>            <tfoot>            <tr>                <th colspan="4"><a href="#" class="add button"><?php _e('Add Cost', 'woocommerce'); ?></a> <a href="#"                                                                                                              class="remove button"><?php _e('Delete selected costs', 'woocommerce'); ?></a>                </th>            </tr>            </tfoot>        </table>        <script type="text/javascript">            jQuery(document).ready(function(){                jQuery('#<?php  echo $this->id; ?>_wc_cart_rates').on('click','a.add',function(){                    var size = jQuery('#<?php echo $this->id; ?>_wc_cart_rates tbody .wc_cart_rates').size();                    var id = parseInt(size) + 1;                        jQuery('<tr class="wc_cart_rates">\								<th class="check-column"><input type="checkbox" name="select[' + id + ']" /></th>\								<td class="wc_cart_rates_class">\									<select name="default_wc_cart_condition[' + id + ']" class="select">\                                        <option value="0">Select Condition</option>\                                        <option value="1">Cart Amount Less than or equal to</option>\                                        <option value="2">Cart Amount Greater than or equal to</option>\                                    </select>\						   		</td>\								<td><input type="text" name="default_wc_cart_condition_value[' + id + ']" placeholder="Condition Value" size="20" /></td>\								<td><input type="text" name="default_wc_cart_cost[' + id + ']" placeholder="Shipping Cost" size="20" /></td>\						</tr>').appendTo('#<?php  echo $this->id; ?>_wc_cart_rates table tbody');                    return false;                });                jQuery('#<?php  echo $this->id; ?>_wc_cart_rates').on('click','a.remove',function(){                    var answer = confirm("<?php  _e( 'Delete the selected rates?', 'woocommerce' ); ?>");                    if (answer) {                        jQuery('#<?php  echo $this->id; ?>_wc_cart_rates table tbody tr th.check-column input:checked').each(function (i, el) {                            jQuery(el).closest('tr').remove();                        });                    }                    return false;                });             });        </script>    </td></tr>